Else {
	Set tError = $select(pIn: $$$TextHTML("Unable to display IO Log Input Data."), 1: $$$TextHTML("Unable to display IO Log Output Data."))
	&html<<div class="Text1" style="margin: 3px;">#(tError)#</div>>
