	Else {
		Set tChild=$$$NULLOREF For { Set tChild=..NextChild(tChild,tRef)  Quit:tChild=$$$NULLOREF
			Set tLen=tChild.GetLengthCRC(tLen,.pCRC, tSeparators,.pSequenceNumber,.pMSHEncoding)
		}
	}
